mapper.select:
  	SELECT FREE_IDX, FREE_TITLE, FREE_CONT, USER_ID, FREE_REGDATE, FREE_READCOUNT
	 FROM FREE_BOARD
	 WHERE FREE_IDX = #{free_idx}

mapper.insert:
INSERT INTO FREE_BOARD
(FREE_TITLE, FREE_CONT,USER_ID, FREE_REGDATE, FREE_READCOUNT)
VALUES(#{free_title}, #{free_cont}, #{user_id}, NOW(), 0);
