mapper.select:
SELECT USER_ID
FROM  USER_INFO
WHERE  USER_ID = #{user_id}

mapper.update:
  		 UPDATE   USER_INFO 
		 SET  	 USER_NAME  = #{user_name}
		  		,USER_PHONE = #{user_phone}
		  		,USER_MAIL  = #{user_mail}
		 WHERE   USER_IDX = #{user_idx}

UPDATE   USER_INFO 
SET   USER_PWD = #{user_pwd}
WHERE USER_ID = #{user_id}

mapper.select:
SELECT count(*)
FROM ATTENDANCECHECK
WHERE USERID = #{userid}
AND ATTENDANCECHECK_DAY = #{today}

SELECT USERID, ATTENDANCECHECK_DAY
FROM ATTENDANCECHECK
WHERE USERID = #{userid}
