mapper.select:
SELECT *
FROM  USER_INFO
WHERE  USER_ID = #{user_id}
AND  USER_PWD = #{user_pwd}

SELECT USER_ID
FROM  USER_INFO
WHERE  USER_ID = #{user_id}

mapper.update:
  		 UPDATE   USER_INFO 
		 SET  	 USER_NAME  = #{user_name}
		  		,USER_PHONE = #{user_phone}
		  		,USER_MAIL  = #{user_mail}
		 WHERE   USER_IDX = #{user_idx}

mapper.select:
	SELECT USER_IDX
	FROM   USER_INFO
	WHERE  USER_ID = #{user_id}
